pub enum Definition {
    Array {
        length: u32,
        elements: Declaration,
    },
    Sequence {
        elements: Declaration,
    },
    Tuple {
        elements: Vec<Declaration>,
    },
    Enum {
        variants: Vec<(VariantName, Declaration)>,
    },
    Struct {
        fields: Fields,
    },
}

pub enum Fields {
    NamedFields(Vec<(FieldName, Declaration)>),
    UnnamedFields(Vec<Declaration>),
    Empty,
}
